import React, { useState } from "react";
import StarBorderIcon from "@mui/icons-material/StarBorder";
import DynamicFeedIcon from "@mui/icons-material/DynamicFeed";
import SearchIcon from "@mui/icons-material/Search";
import MailOutlineIcon from "@mui/icons-material/MailOutline";
import { Link } from "react-router-dom";
import "./MailContent.css";
import { Box, Button, Modal, Typography } from "@mui/material";

function Mail() {
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "1px solid #000",
    boxShadow: 2,
    pt: 2,
    px: 4,
    pb: 3,
  };

  return (
    <div className="mail items-center border-b-[0.1px] relative border-gray-200">
      <input className="" type="checkbox" />
      <button>
        <StarBorderIcon
          className="ml-5"
          sx={{ color: "#B2B2B4", fontSize: "22px" }}
        />
      </button>

      <div className="pl-4 flex">
        <button>
          <MailOutlineIcon
            className="mr-10"
            sx={{ color: "#B2B2B4", fontSize: "19px" }}
          />
        </button>
        <button
          onClick={handleOpen}
          className="sender text-left text-[12px] hover:underline"
        >
          발신자ID
        </button>
      </div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        // aria-describedby="modal-modal-description"
      >
        <Box sx={{ ...style, width: 350 }}>
          <Typography id="modal-modal-title" variant="h6" component="h2">
            Text in a modal
          </Typography>
          <Button onClick={handleClose}>X</Button>
          <Typography id="modal-modal-description" sx={{ mt: 0 }}>
            Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
          </Typography>
        </Box>
      </Modal>

      <div className="text-[13px] w-[870px] flex items-center justify-between">
        <div className="ml-2">
          <Link to="/mail/detail" className="hover:underline">
            이메일 내용입니다.
          </Link>
          <SearchIcon sx={{ color: "black", fontSize: "15px" }} />
          <DynamicFeedIcon sx={{ color: "black", fontSize: "15px" }} />
        </div>
        <div className="flex-end text-[12px]">오후 01:16</div>
      </div>
    </div>
  );
}

export default Mail;
